const INITIAL_STATE = {
  row: 0,
  col: 0,
  dir: 0,
  selected_row: 0,
  selected_col: 0,
  selected_dir: 0
};

const operationsReducer = (state = INITIAL_STATE, action)  => {
  //alert(`CALL ${action.type} ${action.rows}`);
  switch (action.type) {
    case 'PARK':
      return { ...state, row: selected_row, col: selected_col, dir:action.selected_dir };
    case 'SELECT_ROW':
      return { ...state, selected_row: action.row};
    default: return state;
  }
}

export default operationsReducer;
