import React from 'react';

const Park = ({verb, onParkSelectRow, onParkSelectCol, onParkSelectDir, rows, cols, row,col,dir}) =>{
  let rowsOptions = [];
  let colsOptions = [];
  for(let r = 1; r<= rows; r++){
    rowsOptions.push(<option value={r}>{r} </option>); 
  }
  for(let c = 1; c<= cols; c++){
    colsOptions.push(<option value={c}>{c} </option>); 
  }
  return <div className='col'>
      <p>Parking Operations</p>
      <select onChange={e => onParkSelectRow(e.target.value)} value={row}>
            <option value="0">Select Row</option>
            {rowsOptions}
       </select>
         <select onChange={e => onParkSelectCol(e.target.value)} value={col}>
            <option value="0">Select Col</option>
            {colsOptions}
         </select>
         <select onChange={e => onParkSelectDir(e.target.value)} value={dir}>
            <option value="1">UP</option>
            <option value="2">DOWN</option>
            <option value="3">RIGH</option>
            <option value="4">LEFT</option>
         </select>
      <button className='btn btn-info' onClick={() => verb()}>PARK</button>
  </div>
}
export default Park;
