const INITIAL_STATE = {
  rows: 5,
  cols: 5,
  selected_rows: 5,
  selected_cols: 5
};

const settingsReducer = (state = INITIAL_STATE, action)  => {
  //alert(`CALL ${action.type} ${action.rows}`);
  switch (action.type) {
    case 'RESET':
      action.init_park({type: 'PARK_INIT'});
      return { ...state, rows: state.selected_rows, cols:state.selected_cols};
    case 'SELECT_ROWS':
      return { ...state, selected_rows: action.rows};
    case 'SELECT_COLS':
      return { ...state, selected_cols: action.cols};
    default: return state;
  }
}

export default settingsReducer;
