const INITIAL_STATE = {
  row: 0,
  col: 0,
  dir: 0,
  selected_row: 0,
  selected_col: 0,
  selected_dir: 0
};

const operationsReducer = (state = INITIAL_STATE, action)  => {
  switch (action.type) {
    case 'PARK':
      return { ...state, rows: action.rows, cols:action.cols };
    default: return state;
  }
}

export default operationsReducer;
