import React from 'react';
import { connect } from 'react-redux';
import Reset from './Reset';
import Park from './Park';
import Parking from './Parking';

const App = ({settings, operations, onReset,onSelectRows, onSelectCols, onPark,onParkInit, onParkSelectRow,onParkSelectCol, onParkSelectDir}) =>
  <div>
      <div> SETTINGS: {JSON.stringify(settings)}</div>
      <div> OPERATIONS: {JSON.stringify(operations)}</div>
      <p>CAR PARK TASK</p>
        <Reset value={settings} verb={onReset} onSelectRows={onSelectRows} onSelectCols={onSelectCols} row={settings.selected_rows} col={settings.selected_cols}/> 
          <Park rows={settings.rows} cols={settings} row={operations.row} col={operations.col}/>

        <Parking  settingsValue={settings} operationsValue={operations} />
  </div>

const mapStateToProps = state => ({
  settings: state.settingsState,
  operations: state.operationsState
});

const mapDispatchToProps = dispatch => ({
  onReset: () => dispatch({ type: 'RESET'}),
  onSelectRows: (rows) => dispatch({ type: 'SELECT_ROWS', rows: rows}),
  onSelectCols: (cols) => dispatch({ type: 'SELECT_COLS', cols:cols}),
  onPark:  () => dispatch({ type: 'PARK'}),
  onParkInit:  () => dispatch({ type: 'PARK_INIT'}),
  onParkSelectRow: (row) => dispatch({ type: 'SELECT_ROW',row:row}),
  onParkSelectCol: (col) => dispatch({ type: 'SELECT_COL',col:col}),
  onParkSelectDir: (dir) => dispatch({ type: 'SELECT_DIR',dir:dir})
});

export default connect(mapStateToProps, mapDispatchToProps)(App);
