import React, {useState} from 'react';

const [row, setRow] = useState("row");
const [col, setCol] = useState("col");

const onRowChange = (row) => {
  //alert(`row ${row.target.value}`);
  setRow(row.target.value);
}

const onColChange = (col) => {
  //alert(`col ${col.target.value}`);
  setCol(col.target.value);
}

const handleVerb = (verb) => {
  alert(`verb ${verb}`);
  verb(row,col);
}

const Reset = ({value, verb}) =>
  <div>
      <p>RESET COMPONENT</p>
      <div>
         ROWS:
         <select onChange={onRowChange}>
            <option value="1">1</option>
            <option value="2">2</option>
            <option value="3">3</option>
            <option value="4">4</option>
            <option value="5">5</option>
            <option value="6">6</option>
            <option value="7">7</option>
            <option value="8">8</option>
            <option value="9">9</option>
            <option value="10">10</option>
         </select>
      </div>
      <div>
         COLS:
         <select onChange={onColChange}>
            <option value="1">1</option>
            <option value="2">2</option>
            <option value="3">3</option>
            <option value="4">4</option>
            <option value="5">5</option>
            <option value="6">6</option>
            <option value="7">7</option>
            <option value="8">8</option>
            <option value="9">9</option>
            <option value="10">10</option>
         </select>
      </div>
      <button onClick={() => verb(row,col)}>RESET</button>
  </div>
export default Reset;
